import React, {Component} from "react";


interface SkillMatchState {
    searchValue:string
}

class SkillMatch extends Component<{}, SkillMatchState>{
    constructor(props: any) {
        super(props);
        this.state = {
            searchValue:"Python"
        };
        this.onInputChange = this.onInputChange.bind(this);
    }


    async search() {
        try {
            let response = await fetch("http://localhost:4567/skillmatch?skills=" + this.state.searchValue);
            if (!response.ok) {
                alert("Failure to get Data.");
                return
            }

            let parsed: any = await response.json();
            for (let entry of Object.keys(parsed)) {
                let value = parsed[entry];
                console.log(value)
            }

        }catch (e) {
            alert(e)
        }
    }

    sea = () => {
        this.search()
    }

    onInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        this.setState({
            searchValue: event.target.value
        });
        console.log("change")
    };

    render() {
        return (
            <div id = "skillmatch">
                <input value={this.state.searchValue}
                       onChange={this.onInputChange}
                />
                <button onClick={this.sea}/>
            </div>
        )
    }

}

export default SkillMatch;
